from django.contrib.auth.models import Userfrom django.test import Client, TestCaseclass UserTestCase(TestCase):    def setUp(self):        User.objects.create(username='flexibi', password='123')    def test_login(self):        user = User.objects.get(username='flexibi')        c = Client()        response = c.post('/users/login/', {'username': 'flexibi', 'password': '123'})        self.assertEqual(user.username, 'flexibi')        self.assertEqual(response.status_code, 200)