"use strict";$(function(){$(".numeric-slider-range").each(function(){const slider=$(this);const sliderId=slider.attr("id");const minInput=$("#"+sliderId+"_min");const maxInput=$("#"+sliderId+"_max");const textDisplay=$("#"+sliderId+"_text");const unit=slider.data("unit");slider.slider({range:true,min:0,max:100,slide:(event,ui)=>{minInput.val(ui.values[0]);maxInput.val(ui.values[1]);textDisplay.text(ui.values[0]+`${unit} - `+ui.values[1]+`${unit}`)},create:()=>{minInput.val(slider.data("cur_min"));maxInput.val(slider.data("cur_max"));slider.slider("option","step",slider.data("step"));slider.slider("option","min",slider.data("range_min"));slider.slider("option","max",slider.data("range_max"));slider.slider("option","values",[slider.data("cur_min"),slider.data("cur_max")])}})})});